import "./App.css";

import { useEffect, useState } from "react";
import { useQuery, gql } from "@apollo/client";

import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import Home from "./pages/Home/Home";

import { WilderType } from "./types/WilderType";
import { SkillType } from "./types/SkillType";

const GET_ALL_WILDERS = gql`
  query GetAllWilders {
    getAllWilders {
      id
      name
      avatar
      city
      grades {
        id
        grade
        skill {
          id
          name
        }
      }
    }
  }
`;

const GET_ALL_SKILLS = gql`
  query GetAllSkills {
    getAllSkills {
      id
      name
    }
  }
`;

const App = () => {
  const [skillsData, setSkillsData] = useState<SkillType[]>([]);

  const dataManipulation = (dataFromApi: any) => {
    const newData = dataFromApi.map(
      (wilder: {
        id: number;
        name: string;
        city: string | null;
        avatar: string | null;
        grades: {
          id: number;
          grade: number;
          skill: { id: number; name: string };
        }[];
      }) => {
        return {
          id: wilder.id,
          name: wilder.name,
          city: wilder.city,
          avatar: wilder.avatar,
          skills: wilder.grades.map((grade) => {
            return {
              id: grade.id,
              grade: grade.grade,
              skill: {
                id: grade.skill.id,
                name: grade.skill.name,
              },
            };
          }),
        };
      }
    );
    return newData;
  };

  // const fetchSkills = async () => {
  //   const skills = await axios.get("http://localhost:5000/api/skill");
  //   setSkillsData(skills.data);
  //   console.log(skills.data);
  // };

  // useEffect(() => {
  //   fetchData();
  //   fetchSkills();
  // }, []);

  const fetchData = async () => {};

  const { loading, error, data } = useQuery(GET_ALL_WILDERS);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error : {error.message}</p>;
  console.log(data);

  return (
    <div className="App">
      <Header />
      <Home
        wildersData={dataManipulation(data.getAllWilders)}
        skillsData={skillsData}
        fetchData={fetchData}
      />
      <Footer />
    </div>
  );
};

export default App;
