import "./App.css";

import { useEffect, useState } from "react";
import { useQuery, gql } from "@apollo/client";

import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import Home from "./pages/Home/Home";

import { WilderType } from "./types/WilderType";
import { SkillType } from "./types/SkillType";
import { async } from "q";

const GET_ALL_WILDERS = gql`
  query GetAllWilders {
    getAllWilders {
      id
      name
      avatar
      city
      grades {
        id
        grade
        skill {
          id
          name
        }
      }
    }
  }
`;

const GET_ALL_SKILLS = gql`
  query GetAllSkills {
    getAllSkills {
      id
      name
    }
  }
`;

const App = () => {
  const dataWildersManipulation = (dataFromApi: any) => {
    const newData = dataFromApi.map(
      (wilder: {
        id: number;
        name: string;
        city: string | null;
        avatar: string | null;
        grades: {
          id: number;
          grade: number;
          skill: { id: number; name: string };
        }[];
      }) => {
        return {
          id: wilder.id,
          name: wilder.name,
          city: wilder.city,
          avatar: wilder.avatar,
          skills: wilder.grades.map((grade) => {
            return {
              id: grade.id,
              grade: grade.grade,
              skill: {
                id: grade.skill.id,
                name: grade.skill.name,
              },
            };
          }),
        };
      }
    );
    return newData;
  };

  // const fetchSkills = async () => {
  //   const skills = await axios.get("http://localhost:5000/api/skill");
  //   setSkillsData(skills.data);
  //   console.log(skills.data);
  // };

  // useEffect(() => {
  //   fetchData();
  //   fetchSkills();
  // }, []);

  const fetchData = async () => {};

  const queryWilders = useQuery(GET_ALL_WILDERS);
  const querySkills = useQuery(GET_ALL_SKILLS);
  console.log("skills", querySkills.data);
  return (
    <div className="App">
      <Header />
      <Home
        wildersData={dataWildersManipulation(queryWilders.data.getAllWilders)}
        skillsData={querySkills.data}
        fetchData={fetchData}
      />
      <Footer />
    </div>
  );
};

export default App;
