import axios from "axios";
import { useState } from "react";

import { Path, useForm, UseFormRegister } from "react-hook-form";

interface UpdateWilderProps {
  id: number;
  name: string;
  city?: string;
  refresh: () => void;
  showUpdateHandler: () => void;
}

interface FormValues {
  name: string;
  city: string;
}

type InputProps = {
  label: Path<FormValues>;
  register: UseFormRegister<FormValues>;
  required: boolean;
  placeholder?: string;
};

const Input = ({ label, register, required, placeholder }: InputProps) => {
  return (
    <>
      <label>{label}</label>
      <input {...register(label, { required })} value={placeholder} />
    </>
  );
};

const UpdateWilder = ({
  id,
  name,
  city,
  refresh,
  showUpdateHandler,
}: UpdateWilderProps) => {
  const { register, handleSubmit, resetField } = useForm<FormValues>();

  const [newName, setNewName] = useState<string>(name);
  const [newCity, setNewCity] = useState<string>(city ? city : "");

  const onSubmit = (data: FormValues) => {
    console.log(data);
    const { name, city } = data;
    axios
      .post("http://localhost:5000/api/wilder/create", { name, city })
      .then((result) => {
        console.log(result);
        refresh();
        showUpdateHandler();
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        axios
          .put("http://localhost:5000/api/wilder/update", {
            id: id,
            name: newName,
            city: newCity,
          })
          .then((result) => {
            console.log(result);
            refresh();
            showUpdateHandler();
          })
          .catch((err) => {
            console.log(err);
          });
      }}
    >
      <label htmlFor="name">Name</label>
      <input
        type="text"
        id="name"
        name="name"
        value={newName}
        onChange={(e) => setNewName(e.target.value)}
      />
      <label htmlFor="city">City</label>
      <input
        type="text"
        id="city"
        name="city"
        value={newCity}
        onChange={(e) => setNewCity(e.target.value)}
      />
      <button type="submit">Add</button>
    </form>
  );
};

export default UpdateWilder;
