import axios from "axios";
import { useState } from "react";
import styles from "./AddWilder.module.css";

import { Path, useForm, UseFormRegister } from "react-hook-form";

interface AddWilderProps {
  refresh: () => void;
}

interface FormValues {
  name: string;
  city: string;
}

type InputProps = {
  label: Path<FormValues>;
  register: UseFormRegister<FormValues>;
  required: boolean;
};

const Input = ({ label, register, required }: InputProps) => {
  return (
    <>
      <label>{label}</label>
      <input {...register(label, { required })} />
    </>
  );
};

const AddWilder = ({ refresh }: AddWilderProps) => {
  const [name, setName] = useState<string>("");
  const [city, setCity] = useState<string>("");

  const { register, handleSubmit } = useForm<FormValues>();

  const onSubmit = (data: FormValues) => {
    console.log(data);

    // axios
    //       .post("http://localhost:5000/api/wilder/create", { name, city })
    //       .then((result) => {
    //         console.log(result);
    //         setName("");
    //         setCity("");
    //         refresh();
    //       })
    //       .catch((err) => {
    //         console.log(err);
    //       });
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit(onSubmit)}>
      <Input label="name" register={register} required />
      <br />
      <Input label="city" register={register} required />
      <label htmlFor="city">City :</label>
      <input
        type="text"
        name="city"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <br />
      <button type="submit">Add Wilder</button>
    </form>
  );
};

export default AddWilder;
