import axios from "axios";
import { useState } from "react";

interface UpdateWilderProps {
  id: number;
  name: string;
  city?: string;
  refresh: () => void;
  showUpdateHandler: () => void;
}

const UpdateWilder = ({
  id,
  name,
  city,
  refresh,
  showUpdateHandler,
}: UpdateWilderProps) => {
  const [newName, setNewName] = useState<string>(name);
  const [newCity, setNewCity] = useState<string>(city ? city : "");

  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        const { name, city } = e.currentTarget;
        console.log(city.value);
        console.log(name);
        axios
          .put("http://localhost:5000/api/wilder/update", {
            id: id,
            name: name,
            city: city.value,
          })
          .then((result) => {
            console.log(result);
            refresh();
            showUpdateHandler();
          })
          .catch((err) => {
            console.log(err);
          });
      }}
    >
      <label htmlFor="name">Name</label>
      <input type="text" id="name" name="name" defaultValue={name} />
      <label htmlFor="city">City</label>
      <input type="text" id="city" name="city" defaultValue={city} />
      <button type="submit">Validate</button>
    </form>
  );
};

export default UpdateWilder;
