import "./App.css";

import { useEffect, useState } from "react";
import { useQuery, gql } from "@apollo/client";

import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import Home from "./pages/Home/Home";

import { WilderType } from "./types/WilderType";
import { SkillType } from "./types/SkillType";

const GET_ALL_WILDERS = gql`
  query GetAllWilders {
    getAllWilders {
      id
      name
      city
      avatar
      skills [{
        title
        grade
      }]
    }
  }
`;

const App = () => {
  const [wildersData, setWildersData] = useState<WilderType[]>([]);
  const [skillsData, setSkillsData] = useState<SkillType[]>([]);

  const dataManipulation = (dataFromApi: any) => {
    const newData = dataFromApi.map(
      (wilder: {
        id: number;
        name: string;
        city: string;
        avatar: string;
        skills: { title: string; grade: number }[];
      }) => {
        const cleanSkills = wilder.skills.map(
          (grade: { grade: number; skill: { name: string } }) => {
            return { title: grade.skill.name, votes: grade.grade };
          }
        );
        return { name: wilder.name, skills: cleanSkills };
      }
    );
    return newData;
  };

  const fetchSkills = async () => {
    const skills = await axios.get("http://localhost:5000/api/skill");
    setSkillsData(skills.data);
    console.log(skills.data);
  };

  useEffect(() => {
    fetchData();
    fetchSkills();
  }, []);

  return (
    <div className="App">
      <Header />
      <Home
        wildersData={wildersData}
        skillsData={skillsData}
        fetchData={fetchData}
      />
      <Footer />
    </div>
  );
};

export default App;
