    const newData = dataFromApi.map(
      (wilder: {
        id: number;
        name: string;
        city: string | null;
        avatar: string | null;
        grades: {
          id: number;
          grade: number;
          skill: { id: number; name: string };
        }[];
      }) => {
        return {
          id: wilder.id,
          name: wilder.name,
          city: wilder.city,
          avatar: wilder.avatar,
          grades: wilder.grades.map((grade) => {
            return {
              title: grade.skill.name,
              grade: grade.grade,
            };
          }),
        };
      }
    );
    return newData;
  };

  const dataSkillsManipulation = (dataFromApi: any) => {
    const newData = dataFromApi.map((skill: { id: number; name: string }) => {
      return {
        id: skill.id,
        title: skill.name,
      };
    });
    return newData;
  };

  // const fetchSkills = async () => {
  //   const skills = await axios.get("http://localhost:5000/api/skill");
  //   setSkillsData(skills.data);
  //   console.log(skills.data);
  // };

