  console.log("wilders", queryWilders.data);
  console.log("skills", querySkills.data);
  // console.log("skills2", dataSkillsManipulation(querySkills.data.getAllSkills));
  return (
    <div className="App">
      <Header />
      <Home
        wildersData={dataWildersManipulation(queryWilders.data)}
        skillsData={test}
        fetchData={fetchData}
      />
      <Footer />
    </div>
  );
};

export default App;
